import React, { useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://localhost:3001';

function App() {
    const [playlists, setPlaylists] = useState([]);
    const [songs, setSongs] = useState([]);
    const [currentSong, setCurrentSong] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [progress, setProgress] = useState(0);

    const audioRef = useRef(null);

    // Fetch initial data
    useEffect(() => {
        // Fetch Playlists
        fetch(`${API_BASE_URL}/api/playlists`)
            .then(res => res.json())
            .then(data => setPlaylists(data))
            .catch(err => console.error("Error fetching playlists:", err));

        // Fetch Songs
        fetch(`${API_BASE_URL}/api/songs`)
            .then(res => res.json())
            .then(data => setSongs(data))
            .catch(err => console.error("Error fetching songs:", err));
    }, []);

    // Effect for handling audio playback
    useEffect(() => {
        if (audioRef.current) {
            if (isPlaying) {
                audioRef.current.play().catch(e => console.error("Play error:", e));
            } else {
                audioRef.current.pause();
            }
        }
    }, [isPlaying, currentSong]);


    const handlePlayPause = (song) => {
        if (currentSong && currentSong.id === song.id) {
            // Toggle play/pause for the current song
            setIsPlaying(!isPlaying);
        } else {
            // Play a new song
            setCurrentSong(song);
            audioRef.current.src = `${API_BASE_URL}${song.audio}`;
            setIsPlaying(true);
        }
    };

    // ... (Your HTML structure from the original file goes here, but as JSX)
    // This is a simplified version for brevity
    return (
        <div className="flex flex-col md:flex-row h-screen overflow-hidden">
            {/* --- Sidebar --- */}
            <div className="sidebar bg-black w-full md:w-16 lg:w-64 flex-shrink-0">
                {/* ... Sidebar content ... */}
                <div className="px-6 py-4 flex-grow overflow-y-auto">
                    <ul>
                        {playlists.map(p => <li key={p.id} className="text-gray-400 truncate mb-2">{p.name}</li>)}
                    </ul>
                </div>
            </div>

            {/* --- Main Content --- */}
            <div className="main-content flex-grow overflow-y-auto bg-gradient-to-b from-gray-900 to-black pb-32">
                {/* ... Header ... */}
                <div className="p-6">
                    <h2 className="text-2xl font-bold mb-6">Made For You</h2>
                    <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-6">
                        {songs.map(song => (
                            <div key={song.id} className="bg-gray-800 p-4 rounded-md hover:bg-gray-700 cursor-pointer" onClick={() => handlePlayPause(song)}>
                                <img src={song.cover} alt={song.title} className="w-full rounded shadow-lg mb-4"/>
                                <h3 className="font-bold">{song.title}</h3>
                                <p className="text-gray-400 text-sm">{song.artist}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* --- Player --- */}
            {currentSong && (
                 <div className="fixed bottom-0 left-0 right-0 bg-gray-900 p-4 flex items-center justify-between border-t border-gray-800">
                     <img src={currentSong.cover} className="w-14 h-14 rounded"/>
                     <div className="ml-4">
                         <h4 className="font-medium">{currentSong.title}</h4>
                         <p className="text-gray-400 text-sm">{currentSong.artist}</p>
                     </div>
                     <button onClick={() => setIsPlaying(!isPlaying)} className="mx-4">
                        <i className={`fas ${isPlaying ? 'fa-pause' : 'fa-play'} text-2xl`}></i>
                     </button>
                 </div>
            )}

            {/* Audio element is hidden but controlled by React */}
            <audio ref={audioRef} />
        </div>
    );
}

export default App;
