import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:3001';

function App() {
    const [playlists, setPlaylists] = useState([]);
    const [songs, setSongs] = useState([]);
    const [currentSong, setCurrentSong] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    const audioRef = useRef(new Audio());

    useEffect(() => {
        // Fetch Playlists
        fetch(`${API_BASE_URL}/api/songs`)
            .then(res => res.json())
            .then(data => {
                setSongs(data);
                setCurrentSong(data[0]); // Set the first song as default
            })
            .catch(err => console.error("Error fetching songs:", err));

        fetch(`${API_BASE_URL}/api/playlists`)
            .then(res => res.json())
            .then(data => setPlaylists(data))
            .catch(err => console.error("Error fetching playlists:", err));
    }, []);

    useEffect(() => {
        const audio = audioRef.current;
        if (isPlaying) {
            audio.play().catch(e => console.error("Error playing audio:", e));
        } else {
            audio.pause();
        }
    }, [isPlaying]);

    const handlePlayPause = (song) => {
        if (currentSong && currentSong.id === song.id) {
            setIsPlaying(!isPlaying);
        } else {
            setCurrentSong(song);
            audioRef.current.src = `${API_BASE_URL}${song.audio}`;
            setIsPlaying(true);
        }
    };

    return (
        <div className="flex h-screen overflow-hidden">
            {/* --- Sidebar --- */}
            <div className="w-64 bg-black flex-shrink-0 flex flex-col border-r border-gray-800">
                <div className="p-6">
                    <div className="flex items-center">
                        <i className="fab fa-spotify text-3xl text-green-500"></i>
                        <span className="ml-2 text-xl font-bold">Spotify</span>
                    </div>
                </div>
                {/* ... other sidebar items ... */}
                <div className="px-6 py-4 flex-grow overflow-y-auto">
                    <ul>
                       {playlists.map(p => <li key={p.id} className="text-gray-400 hover:text-white cursor-pointer truncate mb-2">{p.name}</li>)}
                    </ul>
                </div>
            </div>

            {/* --- Main Content --- */}
            <div className="flex-grow overflow-y-auto bg-gradient-to-b from-gray-900 to-black pb-24">
                {/* ... Header ... */}
                <div className="p-6">
                    <h1 className="text-3xl font-bold mb-6">Good afternoon</h1>
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
                        {songs.map(song => (
                            <div key={song.id} className="bg-gray-800 bg-opacity-40 p-4 rounded-md hover:bg-gray-700 cursor-pointer transition-colors group" onClick={() => handlePlayPause(song)}>
                                <div className="relative mb-4">
                                    <img src={song.cover} alt="Album cover" className="w-full rounded shadow-lg"/>
                                    <button className="absolute bottom-2 right-2 bg-green-500 rounded-full w-12 h-12 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity hover:scale-110">
                                        <i className={`fas ${isPlaying && currentSong.id === song.id ? 'fa-pause' : 'fa-play'} text-black text-xl`}></i>
                                    </button>
                                </div>
                                <h3 className="font-bold truncate">{song.title}</h3>
                                <p className="text-gray-400 text-sm">{song.artist}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* --- Player --- */}
            {currentSong && (
                <div className="fixed bottom-0 left-0 right-0 bg-gradient-to-b from-gray-800 to-black border-t border-gray-700 p-4 flex items-center justify-between">
                    <div className="flex items-center w-1/4">
                        <img src={currentSong.cover} alt="Album cover" className={`w-14 h-14 rounded mr-3 ${isPlaying ? 'playing-animation' : ''}`} />
                        <div>
                            <h4 className="font-medium text-sm">{currentSong.title}</h4>
                            <p className="text-gray-400 text-xs">{currentSong.artist}</p>
                        </div>
                    </div>
                    <div className="flex flex-col items-center">
                        <button onClick={() => setIsPlaying(!isPlaying)} className="bg-white rounded-full w-8 h-8 flex items-center justify-center hover:scale-105">
                            <i className={`fas ${isPlaying ? 'fa-pause' : 'fa-play'} text-black`}></i>
                        </button>
                        {/* Progress bar would go here */}
                    </div>
                    <div className="w-1/4"></div>
                </div>
            )}
        </div>
    );
}

export default App;
