import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:3001';

function App() {
    const [playlists, setPlaylists] = useState([]);
    const [songs, setSongs] = useState([]);
    const [currentSong, setCurrentSong] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    const audioRef = useRef(new Audio());

    // Fetch data on component mount
    useEffect(() => {
        fetch(`${API_BASE_URL}/api/songs`)
            .then(res => res.json())
            .then(data => setSongs(data))
            .catch(err => console.error("Error fetching songs:", err));

        fetch(`${API_BASE_URL}/api/playlists`)
            .then(res => res.json())
            .then(data => setPlaylists(data))
            .catch(err => console.error("Error fetching playlists:", err));
    }, []);

    // Effect to handle audio playback
    useEffect(() => {
        const audio = audioRef.current;
        if (isPlaying && currentSong) {
            audio.src = `${API_BASE_URL}${currentSong.audio}`;
            audio.play().catch(e => console.error("Error playing audio:", e));
        } else {
            audio.pause();
        }
    }, [isPlaying, currentSong]);


    const handleSongClick = (song) => {
        if (currentSong && currentSong.id === song.id) {
            setIsPlaying(!isPlaying); // Toggle play/pause
        } else {
            setCurrentSong(song);
            setIsPlaying(true); // Play new song
        }
    };

    return (
        <div className="flex flex-col md:flex-row h-screen overflow-hidden">
            {/* Sidebar */}
            <div className="sidebar bg-black w-full md:w-64 flex-shrink-0 flex flex-col border-r border-gray-800">
                <div className="p-6">
                    <div className="flex items-center"><i className="fab fa-spotify text-3xl text-green-500"></i><span className="ml-2 text-xl font-bold hidden md:inline">Spotify</span></div>
                </div>
                <div className="px-6 py-4"><ul className="text-gray-400"> {/* Sidebar links */} </ul></div>
                <div className="px-6 py-4 flex-grow overflow-y-auto">
                    {playlists.map(p => <div key={p.id} className="text-gray-400 hover:text-white cursor-pointer truncate py-1">{p.name}</div>)}
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content flex-grow overflow-y-auto bg-gradient-to-b from-gray-900 to-black pb-32">
                <header className="sticky top-0 z-10 p-4 flex justify-between items-center bg-gray-900 bg-opacity-50 backdrop-blur-md"> {/* Header content */} </header>
                <div className="p-6">
                    <h1 className="text-3xl font-bold mb-6">Made For You</h1>
                    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-6">
                        {songs.map(song => (
                            <div key={song.id} onClick={() => handleSongClick(song)} className="bg-gray-800 bg-opacity-40 p-4 rounded-md hover:bg-gray-700 cursor-pointer transition-colors group">
                                <div className="relative mb-4">
                                    <img src={song.cover} alt={song.title} className="w-full rounded shadow-lg" />
                                    <button className="absolute bottom-2 right-2 bg-green-500 rounded-full p-3 opacity-0 group-hover:opacity-100 transition-opacity hover:scale-110">
                                        <i className={`fas ${isPlaying && currentSong?.id === song.id ? 'fa-pause' : 'fa-play'} text-black`}></i>
                                    </button>
                                </div>
                                <h3 className="font-bold truncate">{song.title}</h3>
                                <p className="text-gray-400 text-sm">{song.artist}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Player */}
            {currentSong && (
                <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800 p-4 flex items-center justify-between">
                    <div className="flex items-center w-1/3">
                        <img src={currentSong.cover} alt={currentSong.title} className={`w-14 h-14 rounded mr-3 ${isPlaying ? 'playing-animation' : ''}`} />
                        <div>
                            <h4 className="font-medium text-sm">{currentSong.title}</h4>
                            <p className="text-gray-400 text-xs">{currentSong.artist}</p>
                        </div>
                    </div>
                    <div className="flex items-center">
                        <button onClick={() => handleSongClick(currentSong)} className="mx-3 bg-white rounded-full w-8 h-8 flex items-center justify-center hover:scale-105">
                            <i className={`fas ${isPlaying ? 'fa-pause' : 'fa-play'} text-black`}></i>
                        </button>
                    </div>
                    <div className="w-1/3"></div>
                </div>
            )}
        </div>
    );
}

export default App;
