const express = require('express');
const cors = require('cors');
const path = require('path');
const app = express();
const PORT = process.env.PORT || 3001;

// Middleware
app.use(cors());
app.use(express.json());
// Serve static audio files from the 'public' directory
app.use(express.static('public'));

// Mock Data
const songs = [
    {
        id: 1,
        title: "Blinding Lights",
        artist: "The Weeknd",
        cover: "data:image/jpeg;base64,",
        audio: "/audio/blinding-lights.mp3"
    },
    {
        id: 2,
        title: "Save Your Tears",
        artist: "The Weeknd",
        cover: "data:image/jpeg;base64,",
        audio: "/audio/save-your-tears.mp3"
    },
    {
        id: 3,
        title: "Levitating",
        artist: "Dua Lipa",
        cover: "data:image/jpeg;base64,",
        audio: "/audio/levitating.mp3"
    }
];

const playlists = [
    { id: 1, name: "Workout Mix", count: 45 },
    { id: 2, name: "Study Focus", count: 32 },
    { id: 3, name: "Road Trip", count: 28 }
];

// API Routes
app.get('/api/songs', (req, res) => {
    res.json(songs);
});

app.get('/api/playlists', (req, res) => {
    res.json(playlists);
});

// Start Server
app.listen(PORT, () => {
    console.log(`✅ API Server running on http://localhost:${PORT}`);
});